// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title FailedContract
 * @dev Contract with compilation errors for testing
 */
contract FailedContract {
    uint256 public value;

    // Error 1: Undefined variable
    function setValueBad() public {
        value = undefinedVariable;
    }

    // Error 2: Type mismatch
    function typeMismatch() public {
        value = "string value";
    }

    // Error 3: Missing return statement
    function missingReturn() public returns (uint256) {
        value = 100;
    }
}
